def find_syslog_msg(pattern):
	s = subprocess.run(["sudo", "grep", pattern,  "/var/log/syslog"],stdout=subprocess.PIPE) 
	if s.returncode != 0:
		print ("grep", pattern, "==>",  str(s))
		# check for other info?
		return False
	return True


def call_makefile(target):
	s = subprocess.run(["make", target],stdout=subprocess.PIPE)
	if s.returncode != 0:
		return False
	return True

def call_load():
	s = subprocess.run(["make", "load"],stdout=subprocess.PIPE)
	if s.returncode != 0:
		return False
	time.sleep(2)
	return True

def call_unload():
	s = subprocess.run(["make", "unload"],stdout=subprocess.PIPE)
	if s.returncode != 0:
		return False
	time.sleep(2)
	return True

