def test_compile(score):
	subprocess.call("make")
	time.sleep(2)
	s = subprocess.run(["ls", "%s.ko"%lkm_name])
	if s.returncode != 0:
		print("LKM [%s] did not compile"%lkm_name)
	else:
		print("compile succeeded: +5")
	#	print (str(s))
		score += 5
	return score



def test_load(score):
	# truncate the system log
	# make load
	# verify message in syslog ("3753 module loaded")
	if not call_load():
		print("loading of LKM failed -- cannot conintue testing")
		return score

	# verify module loaded
	s = subprocess.run("lsmod",stdout=subprocess.PIPE)
	if str(s).find(lkm_name) == -1:
		print("module not listed in LKMs")
	else:
		print("insmod succeeded")

	if find_syslog_msg("CSPB-3753.*loaded LKM") :
		print("module load message detected: +5")
		score += 5
	else:
		print("module load message not detected")

	return score

def test_unload(score):
	if not call_unload():
		print("unloading of LKM failed -- cannot conintue testing")
		return score

	s = subprocess.run("lsmod",stdout=subprocess.PIPE)
	if str(s).find(lkm_name) != -1:
		print("lsmod still list module")
	else:
		print("rmmod succeeded")

	if find_syslog_msg("CSPB-3753.*unloaded LKM") :
		print("module unload message detected: +5")
		score += 5
	else:
		print("module load message failed")


	return score

