def test_open_release(score):
	# open file
	# see open message
	# close
	# see close message

	if not call_makefile("device"):
		print("creation of device failed -- cannot conintue testing")
		return score

	if not call_load():
		print("loading of LKM failed -- cannot conintue testing")
		return score

	f = open("/dev/%s"%device_name, "r")
	time.sleep(1)

	if find_syslog_msg("CSPB-3753.*opened") :
		print("module opened message detected: +5")
		score += 3
	else:
		print("module opened message not found")

	f.close()
	time.sleep(1)

	if find_syslog_msg("CSPB-3753.*released") :
		print("module released message detected: +5")
		score += 2
	else:
		print("module released message not found")

	call_makefile("unload")
	call_makefile("remove")
	
	return score

