def test_read(score):
	# open file
	# see open message
	# close
	# see close message

	if not call_makefile("device"):
		print("creation of device failed -- cannot conintue testing")
		return score

	if not call_load():
		print("loading of LKM failed -- cannot conintue testing")
		return score


	try:
		f = open("/dev/%s"%device_name, "w")
	except:
		print ("file open for write failed")
		call_makefile("unload")
		call_makefile("remove")
		return score

	try:
		f.write("ABCDEFG")
		print ("write completed")
		f.close()
	except: 
		print("module read function failed to write data")
		f.close()
		call_makefile("unload")
		call_makefile("remove")
		return score

	try:
		f = open("/dev/%s"%device_name, "r")
	except:
		print ("file open for read failed")
		call_makefile("unload")
		call_makefile("remove")
		return score

	try:
		s = f.read(3)
		print ("test read returned [%s] %d characters"%(s, len(s)))
		if (len(s) == 3) and (s == "ABC"):
			s = f.read(3)
			print ("test read returned [%s] %d characters"%(s, len(s)))
			if (len(s) == 3) and (s == "DEF"):
				time.sleep(1)
				if find_syslog_msg("CSPB-3753.*reading .* characters") :
					print("module reading characters message detected: +10")
					score += 10
				else:
					print("module reading characters message not found")
					print(str(s))
	except: 
		print("module read function failed")
		f.close()
		call_unload()
		call_makefile("remove")
		return score

	f.close()

	call_makefile("unload")
	call_makefile("remove")
	
	return score


